folderPath.value:
@concat(
    string(
        if(equals(formatDateTime(utcNow(), 'MM'), '01'),
           sub(int(formatDateTime(utcNow(), 'yyyy')), 1),
           int(formatDateTime(utcNow(), 'yyyy'))
        )
    ),
    'q',
    string(
        if(equals(formatDateTime(utcNow(), 'MM'), '01'),
           4,
           div(add(int(formatDateTime(utcNow(), 'MM')), 2), 3)
        )
    )
)
